import React, { useState } from "react";
import './Header.css';

const Header = () => {
    const [selectTheme, setSelectTheme] = useState(null);
    const handleThemeChange = (theme) => {
        setSelectTheme(theme);
        console.log("T覺kland覺:", theme);

       
    };

    const hideThemes = (theme) => {
        if (theme === "theme1") {
            return { theme2: "none", theme3: "none" };
        } else if (theme === "theme2") {
            return { theme1: "none", theme3: "none" };
        } else if (theme === "theme3") {
            return { theme1: "none", theme2: "none" };
        } else {
            return { theme1: "block", theme2: "block", theme3: "block" };
        }
    };

    const themeStyles = {
        theme1: { display: hideThemes(selectTheme).theme1 },
        theme2: { display: hideThemes(selectTheme).theme2 },
        theme3: { display: hideThemes(selectTheme).theme3 }
    };

    return (
        <main className="Header">
            <div className="logo">
                <h1>calc</h1>
            </div>
            <div className="select-theme">
                <h4>THEME</h4>
                <div className="radio-buttons">
                    <input id="theme1" type="radio" checked={selectTheme === 'theme1'} onChange={() => handleThemeChange('theme1')} />
                    <input id="theme2" type="radio" checked={selectTheme ==='theme2'} onChange={() => handleThemeChange('theme2')}/>
                    <input id="theme3" type="radio"checked={selectTheme ==='theme3'} onChange={() => handleThemeChange('theme3')} /> 
                </div>
            </div>
        </main>
    )
}

export default Header;
