import React from "react";
import "./Calc.css";

const Calc = ({ onKeyPress }) => {
    const handleOperation = (operation) => {
        onKeyPress(operation);
    };

    return (
        <main className="Calc">
            <div className="grid-container">
                <div className="grid-item" onClick={() => handleClick("7")}>7</div>
                <div className="grid-item" onClick={() => handleClick("8")}>8</div>
                <div className="grid-item" onClick={() => handleClick("9")}>9</div>
                <div className="grid-item cyan-bg" onClick={() => handleClick("DEL")}>DEL</div>
                <div className="grid-item" onClick={() => handleClick("4")}>4</div>
                <div className="grid-item" onClick={() => handleClick("5")}>5</div>
                <div className="grid-item" onClick={() => handleClick("6")}>6</div>
                <div className="grid-item" onClick={() => handleClick("+")}>+</div>
                <div className="grid-item" onClick={() => handleClick("1")}>1</div>
                <div className="grid-item" onClick={() => handleClick("2")}>2</div>
                <div className="grid-item" onClick={() => handleClick("3")}>3</div>
                <div className="grid-item" onClick={() => handleClick("-")}>-</div>
                <div className="grid-item" onClick={() => handleClick(".")}>,</div>
                <div className="grid-item" onClick={() => handleClick("0")}>0</div>
                <div className="grid-item" onClick={() => handleClick("/")}>/</div>
                <div className="grid-item" onClick={() => handleClick("x")}>x</div>
                <div className="grid-item span-2 cyan-bg" onClick={() => handleClick("RESET")}>RESET</div>
                <div className="grid-item span-2 red-bg" onClick={() => handleClick("=")}>=</div>
            </div>
        </main>
    );
};

export default Calc;
