import React, { useState } from "react";
import './Header.css';

const Header = () => {
    const [selectTheme, setSelectTheme] = useState(null);
    const handleThemeChange = (theme) => {
        setSelectTheme(theme);
        console.log("T覺kland覺:", theme);
    };

    const getDisplayValue = (theme) => {
        return selectedTheme === theme ? 'block' : 'none';
    };

    return (
        <main className="Header">
            <div className="logo">
                <h1>calc</h1>
            </div>
            <div className="select-theme">
                <h4>THEME</h4>
                <div className="radio-buttons">
                    <input id="theme1" type="radio" checked={selectTheme === 'theme1'} onChange={() => handleThemeChange('theme1')} />
                    <input id="theme2" type="radio" checked={selectTheme ==='theme2'} onChange={() => handleThemeChange('theme2')}/>
                    <input id="theme3" type="radio"checked={selectTheme ==='theme3'} onChange={() => handleThemeChange('theme3')} /> 
                </div>
            </div>
        </main>
    )
}

export default Header;
